import api from './api';
import { CourseResponse, StudentCourseResponse, Course } from '../types/course';

export const courseService = {
  // 检查课程冲突
  checkConflict: async (courseId: number) => {
    const response = await api.get<{ has_conflict: boolean; conflict_courses: Course[] }>(
      `/courses/${courseId}/check-conflict/`
    );
    return response.data;
  },

  // 获取课程列表
  getCourses: async (page: number = 1, search: string = '') => {
    const response = await api.get<CourseResponse>('/courses/', {
      params: {
        page,
        search,
      },
    });
    return response.data;
  },

  // 获取课程详情
  getCourseDetail: async (courseId: number) => {
    const response = await api.get<Course>(`/courses/${courseId}/`);
    return response.data;
  },

  // 获取学生已选课程
  getStudentCourses: async (page: number = 1) => {
    const response = await api.get<StudentCourseResponse>('/courses/selected/', {
      params: {
        page,
      },
    });
    return response.data;
  },

  // 选课
  selectCourse: async (courseId: number) => {
    const response = await api.post(`/courses/${courseId}/select/`);
    return response.data;
  },

  // 退课
  dropCourse: async (courseId: number) => {
    const response = await api.post(`/courses/${courseId}/drop/`);
    return response.data;
  },
};
